import './App.css';
import React, { useState, useEffect } from 'react';



function App() {

  const [categories, setCategories] = useState([])

  const [questionNumber, setQuestionNumber] = useState("")
  const [difficulty, setDifficulty] = useState("")
  const [questionType, setQuestionType] = useState("")

  async function fetchData(){
    try{
      const response = await fetch('https://opentdb.com/api_category.php')
      const data = await response.json()
      const dataArray = data.trivia_categories
      setCategories(dataArray)
    }
    catch(error){
      console.error('Error fetching data:', error);
    }
  }

  function isHeld(){
    return 
  }

  const subjectCatalogue =categories.map(category => (

      <button key={category.id}>{category.name}</button>
  )
  ) 


  useEffect(() =>{ 
    fetchData()
  },[])

  return (
    <div className="App">
      <main>
      <div className='header-container'>
        <h1 className='header'>Quizzical</h1>
        <p className='subheader'>Let's get quizzical!</p>
        <button onClick={()=>console.log(categories)}>Hey</button>
      </div>

      <div className='container-container'>
        <div className='category-container'>
          <h3>Category</h3>
          <div className='container-buttons'>
              <button>Any category</button>
              {subjectCatalogue}
          </div>
        </div>

        <div className='questions-number-container'>
          <h3>Number of Questions</h3>
        <div className='container-buttons'>
          <button>10</button>
          <button>20</button>
          <button>30</button>
          <button>40</button>
          <button>50</button>
        </div>

        </div>

        <div className='difficulty-container'>
          <h3>Difficulty</h3>
          <div className='container-buttons'>
            <button>Any difficulty</button>
            <button>Easy</button>
            <button>Medium</button>
            <button>Hard</button>
          </div>

        </div>

        <div className='question-type-container'>
          <h3>Type of Question</h3>
          <div className='container-buttons'>
            <button>Any type</button>
            <button>Multiple</button>
            <button>Boolean(True/False)</button>
          </div>

        </div>


      </div>
      <div className='button-container'>
          <button>Start Game</button>
      </div>
      </main>
    </div>
  );
}

export default App;
