import './App.css';
import React, { useState, useEffect } from 'react';



function App() {

  const [categories, setCategories] = useState([])
  const [gameStarted, setGameStarted] = useState(false)

  const [selectedCategory, setSelectedCategory] = useState("")
  const [questionNumber, setQuestionNumber] = useState("10")
  const [difficulty, setDifficulty] = useState("")
  const [questionType, setQuestionType] = useState("")


  async function fetchData(){
    try{
      const response = await fetch('https://opentdb.com/api_category.php')
      const data = await response.json()
      const dataArray = data.trivia_categories
      setCategories(dataArray)
    }
    catch(error){
      console.error('Error fetching data:', error);
    }
  }

  function handleGameStarted(){
    setGameStarted(prevData => !prevData)
  }


  useEffect(() =>{ 
    fetchData()
  },[])

  return (
    <div className="App">
      {
      gameStarted ? 
      <div>Game Started!</div>
      :
      <main>
      <div className='header-container'>
        <h1 className='header'>Quizzical</h1>
        <p className='subheader'>Let's get quizzical!</p>
        <button onClick={()=>console.log(categories)}>Hey</button>
      </div>

      <div className='container-container'>
        <div className='category-container'>
          <h3>Category</h3>
          <div className='container-buttons'>
          {/* <button 
              onClick={()=> setSelectedCategory("")}
               value="Any category"
               className={selectedCategory === "" ? 'selected' : 'btn-choice'}
               >Any category</button> */}
              {
                  [{id: "", name: "Any Category"},...categories].map((category) => (
                    <button 
                    key = {category.id}
                    value={category.id} 
                    onClick = {()=>setSelectedCategory(category.id)}
                    className={selectedCategory === category.id ? 'selected' : 'btn-choice'}>
                      {category.name }
                      </button>
                  ))
              }
          </div>
        </div>

        <div className='questions-number-container'>
          <h3>Number of Questions</h3>
          <div className='container-buttons'>
          {['10', '20', '30', '40', '50'].map((num) => (
                  <button
                    key={num}
                    value={num}
                    className={questionNumber === num ? 'selected' : 'btn-choice'}
                    onClick={() => setQuestionNumber(num)}
                  >
                    {num}
                  </button>
             )) }
          </div>

        </div>

        <div className='difficulty-container'>
          <h3>Difficulty</h3>
          <div className='container-buttons'>
            {
              ["", "easy", "medium", "hard"].map((level)=>
              (
                <button
                  key={level}
                  value={level}
                  className={difficulty === level ? 'selected' : 'btn-choice'}
                  onClick={()=>setDifficulty(level)}>
                  {level || "Any Difficulty"}
                </button>
              ))
            }
          </div>

        </div>

        <div className='question-type-container'>
          <h3>Type of Question</h3>
          <div className='container-buttons'>
            {["", "multple", "boolean"].map((type)=> (
              <button value={type}
              key={type}
              className= {questionType === type ? 'selected' : 'btn-choice'}
              onClick={()=> setQuestionType(type)}>
                {type || "Any type"}
                </button>
            ))

          }
          </div>

        </div>


      </div>
      <div className='button-container'>
          <button className='start-btn' onClick={handleGameStarted}>Start quiz</button>
      </div>
      </main>
}
    </div>
  );
}

export default App;
